                          // წავშალოთ ატრიბუტი
                          articleDiv.removeAttribute("style");
                          // დავამატოთ ატრიბუტი style
                          articleDiv.setAttribute("style", "color:blue;");
                          styleValue = articleDiv.getAttribute("style");
